create view "dbt_szammit"."scd_ri_policy_period__dbt_tmp" as
    -- This file is automatically generated

with

ri_policy_period as (
    select * from "IGI_PROD_DW"."dbo"."RIPolicyPeriod"
),

ordered as (
    select
        lRIPolicyPeriodKey,
        lRIPolicyKey,
        lRIFolderKey,
        lAccountPeriodKey,
        dw_loadts,
        row_number() over (
            partition by
                lRIPolicyPeriodKey,
                lRIPolicyKey,
                lRIFolderKey,
                lAccountPeriodKey
            order by
                dw_loadts
        ) as dw_extract_order
    from
        ri_policy_period
),

filtered as (
    select * from ordered where dw_extract_order = 1
),

ranged as (
    select
        lRIPolicyPeriodKey,
        lRIPolicyKey,
        lRIFolderKey,
        lAccountPeriodKey,
        dw_loadts as _valid_from,
        lead(dw_loadts) over (partition by lRIPolicyPeriodKey order by dw_loadts) as _valid_to
    from
        filtered
)

select * from ranged
