create view "dbt_dev"."scd_type_of_insurance__dbt_tmp" as
    -- This file is automatically generated

with

type_of_insurance as (
    select * from "IGI_PROD_DW"."dbo"."TYPEOFINSURANCE"
),

ordered as (
    select
        LTYPEOFINSURANCEKEY,
        SCODE,
        STYPEOFINSURANCE,
        STYPEOFINSURANCESEARCH,
        dw_loadts,
        row_number() over (
            partition by
                LTYPEOFINSURANCEKEY,
                SCODE,
                STYPEOFINSURANCE,
                STYPEOFINSURANCESEARCH
            order by
                dw_loadts
        ) as dw_extract_order
    from
        type_of_insurance
),

filtered as (
    select * from ordered where dw_extract_order = 1
),

ranged as (
    select
        LTYPEOFINSURANCEKEY,
        SCODE,
        STYPEOFINSURANCE,
        STYPEOFINSURANCESEARCH,
        dw_loadts as _valid_from,
        lead(dw_loadts) over (partition by LTYPEOFINSURANCEKEY order by dw_loadts) as _valid_to
    from
        filtered
)

select * from ranged
